em a{background: #fff; height: 20px; font-size: 14px;}.ui-menu-item .ui-menu-item-wrapper:hover{border: none !important; background-color: red;}#scrollBack{position: fixed; bottom: 40px; left: 40px; z-index: 100; text-align: center; line-height: 30px; color: #fff; cursor: pointer; border: 0; border-radius: 2px; text-decoration: none; transition: opacity 0.2s ease-out; opacity: 0; width: 70px; height: 70px; padding: 10px 16px; font-size: 24px; line-height: 2.0; border-radius: 35px;}#scrollBack:hover{background: #074481; border: 1px solid white; transition: 0.5s;}#scrollBack.show{opacity: 1;}.btn-circle{width: 49px; height: 49px; text-align: center; padding: 5px 0; font-size: 20px; line-height: 2.00; border-radius: 30px;}.btn-circle-lg{width: 79px; height: 79px; text-align: center; padding: 13px 0; font-size: 30px; line-height: 2.00; border-radius: 70px;}</style><?php include 'footer.html';?><script src="https://code.jquery.com/ui/1.10.4/jquery-ui.js"></script><link href="https://code.jquery.com/ui/1.10.4/themes/ui-lightness/jquery-ui.css" rel="stylesheet"><script type="text/javascript" src="js/slick.min.js"></script><script>
    var searchResult = [];
    $(function () {
        if ($('#scrollBack').length) {
            var scrollTrigger = 100,
                /* px */
                backToTop = function () {
                    var scrollTop = $(window).scrollTop();
                    if (scrollTop > scrollTrigger) {
                        $('#scrollBack').addClass('show');
                    } else {
                        $('#scrollBack').removeClass('show');
                    }
                };
            backToTop();
            $(window).on('scroll', function () {
                backToTop();
            });
            $('#scrollBack').on('click', function (e) {
                e.preventDefault();
                $('html,body').animate({
                    scrollTop: 0
                }, 700);
            });
        }
        $.getJSON("/wui/wui.php?returningCust=1", function (data) {
            if (data.name.trim()) {
                $("#cust_name").val(data.name);
            }
            if (data.phone.trim()) {
                $("#cust_phone").val(data.phone);
            }
            if (data.address.trim()) {
                $("#autocomplete").val(data.address);
            }
        });

        $("#autocomplete").keyup(function () {
            $("#loaderIcon").removeClass("fa-search");
            $("#loaderIcon").addClass("fa-circle-o-notch fa-spin");
        });
    });

    $('#cust_phone').keyup(function () {
        var foo = $(this).val().split("-").join(""); /* remove hyphens */
        if (foo.length > 0) {
            foo = foo.match(/\d{3}(?=\d{2,3})|\d+/g).join("-");
        }
        $(this).val(foo);
    });
    $("#contactForm").submit(function (event) {
        event.preventDefault();
        var name = $("#cust_name").val();
        var phone = $("#cust_phone").val();
        var address = $("#autocomplete").val();
        var phone = phone.replace(/[^0-9\.]/g, '');

        console.log(phone);
        $.post("/wui/wui.php?checkAddress=1", {
            name: name,
            phone: phone,
            address: address
        }).done(function (data) {
            var data = jQuery.parseJSON(data);
            if (data.name == false) {
                $('#myModal').modal('show');
                $('#modal-error').html(
                    'The name is missing or invalid. Please enter your First and Lastname.<br> All fiends are required.<br /> Thank you.'
                );
                return false;
            }
            if (data.phone == false) {
                $('#myModal').modal('show');
                $('#modal-error').html(
                    'The phone number is missing or invalid.<br> All fields are required.<br /> Thank you.'
                );
                return false;
            }
            if (data.address == false) {
                $('#myModal').modal('show');
                $('#modal-error').html('The address field is required. <br /> Thank you.');
                return false;
            }
            if (data.page != false && data.name != false && data.phone != false && data.address !=
                false) {
                window.location.href = data.page;
            }
        }, "json");
    });
    $("#autocomplete").autocomplete({
        source: function (request, response) {
            $.ajax({
                url: "wui/wui.php?getAddress=1",
                dataType: "json",
                data: {
                    search: request.term
                },
                success: function (data) {
                    console.log(data)
                    if (!data) {
                        $("#loaderIcon").addClass("fa-times");
                        $("#loaderIcon").removeClass("fa-circle-o-notch fa-spin");
                    }
                    response($.map(data, function (item) {

                        var formatedAddress = item.address1 + "," + item.city + "," +
                            item.state + "," + item.zip;
                        $("#loaderIcon").addClass("fa-check");
                        $("#loaderIcon").removeClass("fa-circle-o-notch fa-spin");
                        return {
                            label: formatedAddress,
                            value: formatedAddress
                        }
                    }));

                }
            });
        },
        minLength: 1
    });
    $(function () {
        $.ajax({
            url: 'js/yelp.json',
            type: 'get',
            dataType: 'json',
            success: function (data) {
                $(data.reviews).each(function (index, review) {
                    /*        console.log(review.id);*/
                    $("#name" + review.id).text(review.name);
                    $("#content" + review.id).html(review.desc);
                    $("#img" + review.id).attr('src', review.img);
                });
            },
            error: function (e) {
                console.log("Error!")
            }
        });
    });
    /* Slick Carousel */
    $(function () {
        $('.review-carousel').slick({
            dots: true,
            infinite: false,
            slidesToShow: 2,
            slidesToScroll: 2,
            responsive: [{
                breakpoint: 1024,
                settings: {
                    slidesToShow: 1,
                    slidesToScroll: 1,
                    infinite: false,
                    dots: false
                }
            }]
        });
    });
</script>
