				$(function() {

if ($('#scrollBack').length) {
    var scrollTrigger = 100, // px
        backToTop = function () {
            var scrollTop = $(window).scrollTop();
            if (scrollTop > scrollTrigger) {
                $('#scrollBack').addClass('show');
            } else {
                $('#scrollBack').removeClass('show');
            }
        };
    backToTop();
    $(window).on('scroll', function () {
        backToTop();
    });
    $('#scrollBack').on('click', function (e) {
        e.preventDefault();
        $('html,body').animate({
            scrollTop: 0
        }, 700);
    });
}
/* JSON for Reviews */
                $.ajax({
                    url: 'js/yelp.json',
                    type: 'get',
                    dataType: 'json',
                    success: function(data) {
                        $(data.reviews).each(function(index, review) {
                    /*        console.log(review.id);*/
                            $("#name"+review.id).text(review.name);
                            $("#content"+review.id).html(review.desc);
                            $("#img"+review.id).attr('src', review.img);
                        });
                    },
                    error: function(e) {
                        console.log("Error!")
                    }
                });

/* Slick Carousel */ 

                $('.review-carousel').slick({
                    dots: true,
                    infinite: false,
                    slidesToShow: 2,
                    slidesToScroll: 2,
                    responsive: [
                        {
                            breakpoint: 1024,
                            settings: {
                                slidesToShow: 1,
                                slidesToScroll: 1,
                                infinite: false,
                                dots: false
                            }
                        }]
                });

/*  Window Resize change margin on title  */ 
                       
                            var $window = $(window);

                            function resize() {
                                if ($window.width() < 700) {
                                    return $("#title-container").removeClass('mt96');
                                }

                                $("#title-container").addClass('mt96');
                            }

                            $window
                                .resize(resize)
                                .trigger('resize');

/* Video Panels */ 

    $('.video').mouseover(function() {
        $(this).next('.vid-overlay').show("slow");
        $('.vid-overlay, .video').mouseout(function() {
            $('.vid-overlay').hide("slow");
        });
    });

/* Responsive Videos */
    $(".responsive-video").fitVids();

}); /*  end of main Jquery funcition */
</script>
